module.exports = {
 get_my_view_path: function(dir_path) {
   var path = dir_path.split('/');
   path.pop();
   path = path.join('/');
   return `${path}/views/`;
   // var path = __dirname.split('/');
   // path.pop();
   // path = path.join('/') + "/views/";
 },
 set_my_view_route: function(app, dir_path) {
   app.set('views', dir_path);
   // app.set('views', path);
 },
 add_res_file: function(express, app, dir_path, item){
   var res_name = item.split('.')[0];
   var model_name = dir_path.split('/');
   model_name.pop();
   model_name.pop();
   app.use(`/${model_name.pop()}/${res_name}`, express.static(`${dir_path}${item}`));
   // app.use("/Movie/myApp", express.static(path + "myApp.js"));
 }
};
